// https://open-meteo.com/en/docs

// PRE-ALPHA
(function (Scratch) {
  "use strict";

  // default values
  var user_LATITUDE = "0";
  var user_LONGITUDE = "0";
  var tempUnit = "Celsius";
  var windspeedUNIT = "Km/s";
  var forecastdays = "7";

  const icon =
    "data:image/svg+xml;base64,";
  class Weatherize {
    getInfo() {
      return {
        id: "weatherize",
        name: "Weatherize",
        menuIconURI: icon,
        blockIconURI: icon,
        color1: "#0a7044", // #b10481
        blocks: [
          {
            blockType: "label",
            text: "Configuration",
          },
          {
            opcode: "setLocation",
            blockType: Scratch.BlockType.COMMAND,
            text: "set latitude: [LATITUDE] and longitude: [LONGITUDE]",
            arguments: {
              LATITUDE: {
                type: Scratch.ArgumentType.NUMBER,
              },
              LONGITUDE: {
                type: Scratch.ArgumentType.NUMBER,
              },
            },
          },
          {
            opcode: "setTempUnit",
            blockType: Scratch.BlockType.COMMAND,
            text: "set tempature unit to [UNIT]",
            arguments: {
              UNIT: {
                type: Scratch.ArgumentType.STRING,
                menu: "UNIT",
                defaultValue: "celsius",
              },
            },
          },
          {
            opcode: "setWindSpeedUnit",
            blockType: Scratch.BlockType.COMMAND,
            text: "set wind speed unit to [WIND_UNIT]",
            arguments: {
              WIND_UNIT: {
                menu: "WIND_UNIT",
                defaultValue: "Km/h",
              },
            },
          },
          {
            opcode: "setForecastDays",
            blockType: Scratch.BlockType.COMMAND,
            text: "set forecast days to [FORECAST_DAYS] days",
            arguments: {
              FORECAST_DAYS: {
                type: Scratch.ArgumentType.NUMBER,
                menu: "FORECAST_DAYS",
                defaultValue: "7",
              },
            },
          },
          "---",
          {
            opcode: "currSetVARABLES",
            blockType: Scratch.BlockType.REPORTER,
            text: "currently set [WVARS]",
            arguments: {
              WVARS: {
                type: Scratch.ArgumentType.STRING,
                menu: "WVARS",
                defaultValue: "Latitude",
              },
            },
          },
          // debugging things [ temporary ]
          {
            blockType: "label",
            text: "debugging things",
          },
          {
            opcode: "is_sandboxed",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "is sandboxed?",
          },
        ],
        menus: {
          WVARS: {
            acceptReporters: false,
            items: [
              "Latitude",
              "Longitude",
              "Tempature Unit",
              "Wind Speed Unit",
              "Forecast Days",
            ],
          },
          UNIT: {
            acceptReporters: false,
            items: ["Celsius", "Fahrenheit"],
          },
          WIND_UNIT: {
            acceptReporters: false,
            items: ["Km/h", "m/s", "Mph", "Knots"],
          },
          FORECAST_DAYS: {
            acceptReporters: false,
            items: ["1", "3", "7", "14", "16"],
          },
        },
      };
    }
    // configurations
    setLocation(args) {
      user_LATITUDE = args.LATITUDE;
      user_LONGITUDE = args.LONGITUDE;
    }
    setTempUnit(args) {
      tempUnit = args.UNIT;
    }

    setWindSpeedUnit(args) {
      windspeedUNIT = args.WIND_UNIT;
    }
    setForecastDays(args) {
      forecastdays = args.FORECAST_DAYS;
    }
    // currently set blocks
    currSetVARABLES({ WVARS }) {
      if (WVARS === "Latitude") {
        return user_LATITUDE;
      } else if (WVARS === "Longitude") {
        return user_LONGITUDE;
      } else if (WVARS === "Tempature Unit") {
        return tempUnit;
      } else if (WVARS === "Wind Speed Unit") {
        return windspeedUNIT;
      } else if (WVARS === "Forecast Days") {
        return forecastdays;
      }
    }
    // debugging things
    is_sandboxed() {
      return !Scratch.extensions.unsandboxed;
    }
  }

  Scratch.extensions.register(new Weatherize());
})(Scratch);
